import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom'
import ReadPosts from './pages/ReadPosts'
import CreatePost from './pages/CreatePost'
import EditPost from './pages/EditPost'
import { Link } from 'react-router-dom'
import { useState } from 'react';
import PostDetail from './components/PostDetail';


const App = () => {

  // Default sort is to sort by review date
  const [sortType, setSortType] = useState('created_at');  
  const [searchQuery, setSearchQuery] = useState('');

  const descr = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.'

  const posts = [
      {'id':'1', 
      'title': 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è',
      'author':'Harvey Milian', 
      'description': descr},
      {'id':'2', 
      'title': 'Love Lock in Paris üîí',
      'author':'Beauford Delaney', 
      'description':descr},
      {'id':'3', 
      'title': 'Wear Pink on Fridays üéÄ',
      'author':'Onika Tonya', 
      'description':descr},
      {'id':'4', 
      'title': 'Adopt a Dog üê∂',
      'author':'Denise Michelle', 
      'description':descr},
  ]

  // Sets up routes
  let element = useRoutes([
    // {
    //   path: "/",
    //   element:<ReadPosts data={posts}/>
    // },
    {
      path: "/",
      element: <ReadPosts sortType={sortType} searchQuery={searchQuery} />
    },
    {
      path:"/edit/:id",
      element: <EditPost data={posts} />
    },
    {
      path:"/new",
      element: <CreatePost />
    },
    {
      path: "/posts/:id",
      element: <PostDetail />
    }
  ]);

  return ( 

    <div className="App">

      <div className="header">
        <h1> Burn</h1>
        <Link to="/"><button className="headerBtn"> Home Page üîç  </button></Link>
        <Link to="/new"><button className="headerBtn"> Submit a Review </button></Link>
        <button className="headerBtn" onClick={() => setSortType('created_at')}> Newest </button>
        <button className="headerBtn" onClick={() => setSortType('rating')}> Most Popular </button>
        <input
          className= "searchBox"
          type="text"
          placeholder="Search by Workout or Gym..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <p> Get authentic reviews from real people that attended your favorite gym or workout class.</p>
      </div>

        {element}
    </div>

  );
}

export default App;
